<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAttributePermission\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Usecase\Retrieve\ScopeAttributePermissionRetrieveUsecase;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Usecase\Retrieve\ScopeAttributePermissionRetrieveResult;

class ScopeAttributePermissionRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly ScopeAttributePermissionRetrieveUsecase $retrieveUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/scope-attribute-permissions{uid}",
        tags: ["Scope attribute permission"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/ScopeAttributePermissionApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve Scope attribute permission");
        $span = $this->startSpan("Retrieve Scope attribute permission");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapScopeAttributePermission($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapScopeAttributePermission(ScopeAttributePermissionRetrieveResult $value): ScopeAttributePermissionApiDTO
    {
        $this->logDebug("Map entity to output dto for Scope attribute permission");
        $span = $this->startSpan("Map entity to output dto for Scope attribute permission");
        try {
            $securityDomain = $value->getSecurityDomain();
            $securityAttribute = $value->getSecurityAttribute();
            $dto = new ScopeAttributePermissionApiDTO();
            $dto->uid = $value->getUid();
            $dto->securityDomain = $securityDomain ? ['$ref' => $securityDomain->uid()] : null;
            $dto->securityAttribute = $securityAttribute ? ['$ref' => $securityAttribute->uid()] : null;
            $dto->permision = $value->getPermision();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
