<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Application\Policy\Allow\Enable;

use Throwable;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Enable\SecurityAttributeEnableAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class IsAutenticatedEnableAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(SecurityAttributeEnableAllowDecision $proposal): SecurityAttributeEnableAllowDecision
    {
        $this->logDebug("Check IsAutenticatedEnableAllow Security attribute");
        $span = $this->startSpan("Check IsAutenticatedEnableAllow Security attribute");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->allow->allowed) {
                $belongs = !$userContext->anonimous;
                $allowed = $belongs ? Allow::allowed('enable', 'Enabled for user IsAutenticatedEnable') : Allow::disallowed('enable', 'Disabled if not IsAutenticatedEnable');
                $proposal->allow = $allowed;
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
