<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Service\Visibility\TrustedClientVisibilityService;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Service\Visibility\RelyingPartyVisibilityService;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeAttributes;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttribute;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeFilter;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeCursor;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeReadGateway;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeSlide;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityAttributeVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityAttributeReadGateway $readGateway,
        private readonly SecurityAttributeWriteGateway $writeGateway,
        private readonly TrustedClientVisibilityService $trustedClientVisibilityService,
        private readonly RelyingPartyVisibilityService $relyingPartyVisibilityService
    ) {
    }

    public function prepareVisibleDataInList(SecurityAttribute $content): SecurityAttributeAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(SecurityAttribute $content): SecurityAttributeAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(SecurityAttributeAttributes $attributes, ?SecurityAttribute $original = null): SecurityAttributeAttributes
    {
        $this->logDebug("Copy with fixed for Security attribute");
        $span = $this->startSpan("Copy with fixed for  Security attribute");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new SecurityAttributePresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(SecurityAttributeAttributes $attributes, ?SecurityAttribute $original = null): SecurityAttributeAttributes
    {
        $this->logDebug("Copy with hidden for Security attribute");
        $span = $this->startSpan("Copy with hidden for  Security attribute");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|SecurityAttributeRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Security attribute");
        $span = $this->startSpan("Check visibility of an iterator for  Security attribute");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new SecurityAttributeFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, SecurityAttributeRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new SecurityAttributeRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(SecurityAttributeFilter $filter, ?SecurityAttributeCursor $cursor = null): SecurityAttributeSlide
    {
        $this->logDebug("List visibles for Security attribute");
        $span = $this->startSpan("List visibles for  Security attribute");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(SecurityAttributeFilter $filter): int
    {
        $this->logDebug("Count visibles for Security attribute");
        $span = $this->startSpan("Count visibles for  Security attribute");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(SecurityAttributeFilter $filter, ?SecurityAttributeCursor $cursor = null): SecurityAttributeSlide
    {
        $this->logDebug("List visibles for update for Security attribute");
        $span = $this->startSpan("List visibles for update for  Security attribute");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(SecurityAttributeFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Security attribute");
        $span = $this->startSpan("Count visibles for update for  Security attribute");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(SecurityAttributeRef $ref): ?SecurityAttribute
    {
        $this->logDebug("Retrieve visible for Security attribute");
        $span = $this->startSpan("Retrieve visible for  Security attribute");
        try {
            $filter = new SecurityAttributeFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(SecurityAttributeRef $ref): ?SecurityAttribute
    {
        $this->logDebug("Retrieve visible for update for Security attribute");
        $span = $this->startSpan("Retrieve visible for update for  Security attribute");
        try {
            $filter = new SecurityAttributeFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?SecurityAttributeRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Security attribute");
        $span = $this->startSpan("Check fields for fix for Security attribute");
        try {
            $result = $this->dispacher->dispatch(new SecurityAttributeCollectNonEditableFields(SecurityAttribute::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?SecurityAttributeRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Security attribute");
        $span = $this->startSpan("Check fields to hide for  Security attribute");
        try {
            $result = $this->dispacher->dispatch(new SecurityAttributeCollectNonVisibleFields([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(SecurityAttributeAttributes $attributes): SecurityAttributeAttributes
    {
        $this->logDebug("Check visibility of parent references for Security attribute");
        $span = $this->startSpan("Check visibility of parent references for  Security attribute");
        try {
            if ($attributes->getTrustedClient() && !$this->trustedClientVisibilityService->checkVisibility($attributes->getTrustedClient())) {
                throw new NotFoundException("Unknow TrustedClient " . $attributes->getTrustedClient());
            }
            if ($attributes->getRelyingParty() && !$this->relyingPartyVisibilityService->checkVisibility($attributes->getRelyingParty())) {
                throw new NotFoundException("Unknow RelyingParty " . $attributes->getRelyingParty());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(SecurityAttributeFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Security attribute");
        $span = $this->startSpan("Compose visibility filter for  Security attribute");
        try {
            $result = $this->dispacher->dispatch(new SecurityAttributeRestrictFilterToVisibility($filter));
            return $result->securityAttributeFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(SecurityAttribute $value): bool
    {
        $this->logDebug("Check if item is visible for Security attribute");
        $span = $this->startSpan("Check if item is visible for  Security attribute");
        try {
            $result = $this->dispacher->dispatch(new SecurityAttributeVisibilityCheck(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(SecurityAttribute $content, bool $inlist): SecurityAttributeAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Security attribute");
        $span = $this->startSpan("Prepare hidratation to visible data for Security attribute");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new SecurityAttributeEnrichForView($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
