<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Create;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Service\Visibility\SecurityAttributeVisibilityService;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttribute;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityAttributeCreateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityAttributeVisibilityService $visibility,
        private readonly SecurityAttributeWriteGateway $writer
    ) {
    }

    public function allowCreate(): Allow
    {
        $this->logDebug("Check allow of create usecase for Security attribute");
        $span = $this->startSpan("Check allow of create usecase for Security attribute");
        try {
            $result = $this->dispacher->dispatch(new SecurityAttributeCreateAllowDecision(Allow::allowed('create', 'Allowed to create by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function create(SecurityAttributeCreateParams $params): SecurityAttributeCreateResult
    {
        $this->logDebug("Run create usecase for Security attribute");
        $span = $this->startSpan("Run create usecase for Security attribute");
        try {
            $allow = $this->allowCreate();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $this->dispacher->dispatch(new SecurityAttributeCreateCheck($params));
            $enriched = $this->dispacher->dispatch(new SecurityAttributeCreateEnrich($params, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $entity = SecurityAttribute::create($input);
            $result = $this->writer->create(
                $entity,
                fn ($created) =>
                            $this->visibility->checkVisibility($created)
            );
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new SecurityAttributeCreateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
