<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Delete;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Service\Visibility\SecurityAttributeVisibilityService;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityAttributeDeleteUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityAttributeVisibilityService $visibility,
        private readonly SecurityAttributeWriteGateway $writer
    ) {
    }

    public function allowDelete(?SecurityAttributeRef $ref = null): Allow
    {
        $this->logDebug("Check allow of delete usecase for Security attribute");
        $span = $this->startSpan("Check allow of delete usecase for Security attribute");
        try {
            $result = $this->dispacher->dispatch(new SecurityAttributeDeleteAllowDecision(Allow::allowed('delete', 'Allowed to delete by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function delete(string $uid): void
    {
        $this->logDebug("Run delete usecase for Security attribute");
        $span = $this->startSpan("Run delete usecase for Security attribute");
        try {
            $ref = new SecurityAttributeRef($uid);
            $allow = $this->allowDelete($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new SecurityAttributeDeleteCheck($original));
            $deleted = $original->delete();
            $this->writer->delete($deleted);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
