<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Enable;

use RuntimeException;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttribute;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class SecurityAttributeEnableCheck
{
    public function __construct(public readonly SecurityAttribute $current)
    {
    }
    public function reject(RuntimeException|ConstraintFail $fail)
    {
        if ($fail instanceof ConstraintFail) {
            throw ConstraintException::ofFail($fail);
        } else {
            throw $fail;
        }
    }
}
