<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Enable;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Service\Visibility\SecurityAttributeVisibilityService;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityAttributeEnableUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityAttributeVisibilityService $visibility,
        private readonly SecurityAttributeWriteGateway $writer
    ) {
    }

    public function allowEnable(?SecurityAttributeRef $ref = null): Allow
    {
        $this->logDebug("Check allow of Enable usecase for Security attribute");
        $span = $this->startSpan("Check allow of Enable usecase for Security attribute");
        try {
            $result = $this->dispacher->dispatch(new SecurityAttributeEnableAllowDecision(Allow::allowed('enable', 'Allowed to Security attribute by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function enable(string $uid): SecurityAttributeEnableResult
    {
        $this->logDebug("Run Enable usecase for Security attribute");
        $span = $this->startSpan("Run Enable usecase for Security attribute");
        try {
            $ref = new SecurityAttributeRef($uid);
            $allow = $this->allowEnable($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new SecurityAttributeEnableCheck($original));
            $modified = $original->enable();
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new SecurityAttributeEnableResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
