<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway;

use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttribute;

class SecurityAttributeCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(SecurityAttribute $securityAttribute, ?int $size): SecurityAttributeCursor
    {
        $next = clone $this;
        $next->sinceUid = $securityAttribute->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
