<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttribute;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driven\SecurityAttributeReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;

class SecurityAttributeReadGateway
{
    private SecurityAttributeReadRepository $repository;
    public function __construct(ContainerInterface $container, ?SecurityAttributeReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(SecurityAttributeReadRepositoryAdapter::class);
    }
    public function resolve(SecurityAttributeRef $ref): ?SecurityAttribute
    {
        return $this->repository->resolve($ref);
    }
    public function list(?SecurityAttributeFilter $filter = null, ?SecurityAttributeCursor $cursor = null): SecurityAttributeSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(SecurityAttributeFilter $filter): ?SecurityAttribute
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?SecurityAttributeFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?SecurityAttributeFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?SecurityAttribute
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByResourceAndAttribute(string $resource, string $attribute): ?SecurityAttribute
    {
        return $this->repository->findOneByResourceAndAttribute($resource, $attribute);
    }
}
