<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttribute;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driven\SecurityAttributeWriteRepositoryAdapter;

class SecurityAttributeWriteGateway
{
    private SecurityAttributeWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?SecurityAttributeWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(SecurityAttributeWriteRepositoryAdapter::class);
    }
    public function create(SecurityAttribute $entity, ?Closure $verify = null): SecurityAttribute
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(SecurityAttributeRef $ref, SecurityAttribute $entity): SecurityAttribute
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(SecurityAttribute $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?SecurityAttributeFilter $filter = null, ?SecurityAttributeCursor $cursor = null): SecurityAttributeSlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(SecurityAttributeFilter $filter): ?SecurityAttribute
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?SecurityAttributeFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?SecurityAttributeFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
    public function findOneForUpdateByUid(string $uid): ?SecurityAttribute
    {
        return $this->repository->findOneForUpdateByUid($uid);
    }
    public function findOneForUpdateByResourceAndAttribute(string $resource, string $attribute): ?SecurityAttribute
    {
        return $this->repository->findOneForUpdateByResourceAndAttribute($resource, $attribute);
    }
}
