<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeAttributeVO;

trait SecurityAttributeAttributeAccesor
{
    private SecurityAttributeAttributeVO $_attribute;
    public function getAttribute(): string
    {
        return $this->_attribute->value();
    }
    public function attribute(): SecurityAttributeAttributeVO
    {
        return $this->_attribute;
    }
    public function isAttributeChanged(?self $original = null): bool
    {
        return $this->_attribute->value() !== ($original ? $original->_attribute->value() : null);
    }
    public function withAttribute(SecurityAttributeAttributeVO|string $attribute): self
    {
        $copy = clone $this;
        $copy->_attribute = SecurityAttributeAttributeVO::from($attribute);
        return $copy;
    }
}
