<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeRelyingPartyVO;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;

trait SecurityAttributeRelyingPartyAccesor
{
    private ?SecurityAttributeRelyingPartyVO $_relyingParty = null;
    public function getRelyingParty(): ?RelyingPartyRef
    {
        return $this->_relyingParty?->value();
    }
    public function relyingParty(): ?SecurityAttributeRelyingPartyVO
    {
        return $this->_relyingParty;
    }
    public function isRelyingPartyChanged(?self $original = null): bool
    {
        return $this->_relyingParty->value() !== ($original ? $original->_relyingParty->value() : null);
    }
    public function withRelyingParty(SecurityAttributeRelyingPartyVO|RelyingPartyRef|null $relyingParty): self
    {
        $copy = clone $this;
        $copy->_relyingParty = SecurityAttributeRelyingPartyVO::from($relyingParty);
        return $copy;
    }
}
