<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeResourceVO;

trait SecurityAttributeResourceAccesor
{
    private SecurityAttributeResourceVO $_resource;
    public function getResource(): string
    {
        return $this->_resource->value();
    }
    public function resource(): SecurityAttributeResourceVO
    {
        return $this->_resource;
    }
    public function isResourceChanged(?self $original = null): bool
    {
        return $this->_resource->value() !== ($original ? $original->_resource->value() : null);
    }
    public function withResource(SecurityAttributeResourceVO|string $resource): self
    {
        $copy = clone $this;
        $copy->_resource = SecurityAttributeResourceVO::from($resource);
        return $copy;
    }
}
