<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class SecurityAttributeEnabledVO
{
    public static function empty(): SecurityAttributeEnabledVO
    {
        return new SecurityAttributeEnabledVO(null);
    }
    public static function from(SecurityAttributeEnabledVO|bool|null $value): SecurityAttributeEnabledVO
    {
        if (is_a($value, SecurityAttributeEnabledVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?SecurityAttributeEnabledVO
    {
        if (is_a($value, SecurityAttributeEnabledVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new SecurityAttributeEnabledVO($value);
        } elseif (is_bool($value)) {
            return new SecurityAttributeEnabledVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['enabled'], [$value], ['bool']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?bool $enabled
    ) {
    }
    public function value(): ?bool
    {
        return $this->enabled;
    }
}
