<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeReadRepository;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeSlide;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeFilter;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeCursor;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttribute;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityAttributeReadRepositoryAdapter implements SecurityAttributeReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityAttributePdoConnector $conn,
    ) {
    }
    #[Override]
    public function resolve(SecurityAttributeRef $ref): ?SecurityAttribute
    {
        if ($ref instanceof SecurityAttribute) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new SecurityAttributeFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?SecurityAttributeFilter $filter = null, ?SecurityAttributeCursor $sort = null): SecurityAttributeSlide
    {
        $this->logDebug("List for Security attribute on adapter ");
        $span = $this->startSpan("List for Security attribute on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new SecurityAttributeSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new SecurityAttributeCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?SecurityAttributeFilter $filter): ?SecurityAttribute
    {
        $this->logDebug("Retrieve for Security attribute on adapter ");
        $span = $this->startSpan("Retrieve for Security attribute on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?SecurityAttributeFilter $filter): bool
    {
        $this->logDebug("Exists for Security attribute on adapter ");
        $span = $this->startSpan("Exists for Security attribute on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?SecurityAttributeFilter $filter = null): int
    {
        $this->logDebug("Count for Security attribute on adapter ");
        $span = $this->startSpan("Count for Security attribute on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?SecurityAttribute
    {
        $this->logDebug("Find on by uid for Security attribute on adapter");
        $span = $this->startSpan("Find on by uid for Security attribute on adapter");
        try {
            return $this->conn->retrieve(new SecurityAttributeFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByResourceAndAttribute(string $resource, string $attribute): ?SecurityAttribute
    {
        $this->logDebug("Find on by resource attribute for Security attribute on adapter");
        $span = $this->startSpan("Find on by resource attribute for Security attribute on adapter");
        try {
            return $this->conn->retrieve(new SecurityAttributeFilter(resourceAndAttribute: SecurityAttributeFilter::resourceAndAttributeFilter(resource: $resource, attribute: $attribute)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
