namespace Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeWriteRepository;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeSlide;
