    }
    #[Override]
    public function resolveForUpdate(SecurityAttributeRef $ref): ?SecurityAttribute
    {
        return $this->conn->retrieve(new SecurityAttributeFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?SecurityAttributeFilter $filter = null, ?SecurityAttributeCursor $sort = null): SecurityAttributeSlide
    {
        $this->logDebug("Count for Security attribute on adapter ");
        $span = $this->startSpan("Count for Security attribute on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new SecurityAttributeSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new SecurityAttributeCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(SecurityAttributeFilter $filter): ?SecurityAttribute
    {
        $this->logDebug("Count for Security attribute on adapter ");
        $span = $this->startSpan("Count for Security attribute on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?SecurityAttributeFilter $filter): bool
    {
        $this->logDebug("Count for Security attribute on adapter ");
