        $span = $this->startSpan("Find on by resource attribute for Security attribute on adapter");
        try {
            return $this->conn->retrieveForUpdate(new SecurityAttributeFilter(resourceAndAttribute: SecurityAttributeFilter::resourceAndAttributeFilter(resource: $resource, attribute: $attribute)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(SecurityAttribute $entity)
    {
        $this->logDebug("Count for Security attribute on adapter ");
        $span = $this->startSpan("Count for Security attribute on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
