<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Create\SecurityAttributeCreateUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\List\SecurityAttributeListUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Update\SecurityAttributeUpdateUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Delete\SecurityAttributeDeleteUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Retrieve\SecurityAttributeRetrieveUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Disable\SecurityAttributeDisableUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Enable\SecurityAttributeEnableUsecase;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityAttributeAllowController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityAttributeCreateUsecase $createUsecase,
        private readonly SecurityAttributeListUsecase $listUsecase,
        private readonly SecurityAttributeUpdateUsecase $updateUsecase,
        private readonly SecurityAttributeDeleteUsecase $deleteUsecase,
        private readonly SecurityAttributeRetrieveUsecase $retrieveUsecase,
        private readonly SecurityAttributeDisableUsecase $disableUsecase,
        private readonly SecurityAttributeEnableUsecase $enableUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/me/acl/access/security-attributes",
        tags: ["Security attribute access control"],
        description:"Allows",
        responses: [
        new OA\Response(response: 200, description: "Ok"),
    ]
    )
    ]
    public function genericAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check generic allow for Security attribute");
        $span = $this->startSpan("Generic allow for Security attribute");
        try {
            $value = [
              'allows' => [
                $this->createUsecase->allowCreate(),
                $this->listUsecase->allowList(),
                $this->updateUsecase->allowUpdate(),
              $this->deleteUsecase->allowDelete(),
              $this->retrieveUsecase->allowRetrieve(),
              $this->disableUsecase->allowDisable(),
              $this->enableUsecase->allowEnable(),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields(),
                'noVisibles' => $this->retrieveUsecase->hiddenFields()
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[OA\Get(
        path: "/api/me/acl/access/security-attributes/{uid}",
        tags: ["Security attribute access control"],
        description:"Allows",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok"),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function contextualAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check specific allow for Security attribute");
        $span = $this->startSpan("Specific allow for Security attribute");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $ref = new SecurityAttributeRef(uid: $args['uid']);
            $value = [
              'allows' => [
                $this->updateUsecase->allowUpdate($ref),
              $this->deleteUsecase->allowDelete($ref),
              $this->retrieveUsecase->allowRetrieve($ref),
              $this->disableUsecase->allowDisable($ref),
              $this->enableUsecase->allowEnable($ref),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields($ref),
                'noVisibles' => $this->retrieveUsecase->hiddenFields($ref)
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
