    #[OA\Post(
        path: "/api/access/security-attributes",
        tags: ["Security attribute"],
        description:"Create",
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityAttributeApiDTO")),
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityAttributeApiDTO")),
              new OA\Response(response: 422, description: "Constraint error"),
          ]
    )]
    public function create(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Create Security attribute");
        $span = $this->startSpan("Create Security attribute");
        $this->sql->begin();
        try {
            $value = $this->readSecurityAttribute($request);
            $result = $this->createUsecase->create($value);
            $this->sql->commit();
            $value = $this->mapSecurityAttribute($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
