    private function readSecurityAttribute(ServerRequestInterface $request): SecurityAttributeCreateParams
    {
        $this->logDebug("Read entity for Security attribute");
        $span = $this->startSpan("Read entity for Security attribute");
        try {
            $body = $request->getParsedBody();
            $errorsList = new ConstraintFailList();
            $value = new SecurityAttributeCreateParams();
            $value->uid(SecurityAttributeUidVO::tryFrom($body['uid'] ?? null, $errorsList));
            $trustedClient = $body['trustedClient'] ?? null;
            if ($trustedClient && isset($trustedClient['$ref'])) {
                $value->trustedClient(SecurityAttributeTrustedClientVO::tryFrom(new TrustedClientRef(uid: $trustedClient['$ref']), $errorsList));
            }
            $relyingParty = $body['relyingParty'] ?? null;
            if ($relyingParty && isset($relyingParty['$ref'])) {
                $value->relyingParty(SecurityAttributeRelyingPartyVO::tryFrom(new RelyingPartyRef(uid: $relyingParty['$ref']), $errorsList));
            }
            $value->resource(SecurityAttributeResourceVO::tryFrom($body['resource'] ?? null, $errorsList));
            $value->attribute(SecurityAttributeAttributeVO::tryFrom($body['attribute'] ?? null, $errorsList));
            $value->enabled(SecurityAttributeEnabledVO::tryFrom($body['enabled'] ?? null, $errorsList));
            $value->readVisibility(SecurityAttributeReadVisibilityVO::tryFrom($body['readVisibility'] ?? null, $errorsList));
            $value->writeVisibility(SecurityAttributeWriteVisibilityVO::tryFrom($body['writeVisibility'] ?? null, $errorsList));
            $value->version(SecurityAttributeVersionVO::tryFrom($body['version'] ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function mapSecurityAttribute(SecurityAttributeCreateResult $value): SecurityAttributeApiDTO
    {
        $this->logDebug("Map entity to output dto for Security attribute");
        $span = $this->startSpan("Map entity to output dto for Security attribute");
        try {
            $trustedClient = $value->getTrustedClient();
            $relyingParty = $value->getRelyingParty();
            $dto = new SecurityAttributeApiDTO();
            $dto->uid = $value->getUid();
            $dto->trustedClient = $trustedClient ? ['$ref' => $trustedClient->uid()] : null;
            $dto->relyingParty = $relyingParty ? ['$ref' => $relyingParty->uid()] : null;
            $dto->resource = $value->getResource();
            $dto->attribute = $value->getAttribute();
            $dto->enabled = $value->getEnabled();
            $dto->readVisibility = $value->getReadVisibility();
            $dto->writeVisibility = $value->getWriteVisibility();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
