<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Enable\SecurityAttributeEnableUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\Gateway\SecurityAttributeFilter;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Batch\SecurityAttributeTaskEnable;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Enable\SecurityAttributeEnableResult;

class SecurityAttributeEnableController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly Runner $runner,
        private readonly SqlTemplate $sql,
        private readonly SecurityAttributeEnableUsecase $enableUsecase,
    ) {
    }
    #[OA\Patch(
        path: "/api/access/security-attributes/{uid}/enable",
        tags: ["Security attribute"],
        description: "Update the value",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityAttributeApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function enable(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("enable for Security attribute");
        $span = $this->startSpan("enable for Security attribute");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->enableUsecase->enable($uid);
            $this->sql->commit();
            $value = $this->mapSecurityAttribute($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }
    public function enableAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("enable all for query Security attribute");
        $span = $this->startSpan("enable all for query Security attribute");
        try {
            $params = $request->getQueryParams();
            $filter = new SecurityAttributeFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                resource: $params['resource'] ?? null,
                trustedClient: isset($params['trusted-client']) ? new TrustedClientRef($params['trusted-client']) : null,
                trustedClients: isset($params['trusted-clients']) ? explode(",", $params['trusted-clients']) : null,
                relyingParty: isset($params['relying-party']) ? new RelyingPartyRef($params['relying-party']) : null,
                relyingPartys: isset($params['relying-partys']) ? explode(",", $params['relying-partys']) : null,
            );
            $res = $this->runner->run(SecurityAttributeTaskEnable::class, ['filter' => $filter]);
            $response->getBody()->write(json_encode($res));
            return $response;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function checkEnableAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check enable all for query Security attribute");
        $span = $this->startSpan("Check enable all for query Security attribute");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $info = $this->runner->detail(new TaskKey($uid));
            if (!$info) {
                throw new NotFoundException('-');
            }
            $response->getBody()->write(json_encode($info));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapSecurityAttribute(SecurityAttributeEnableResult $value): SecurityAttributeApiDTO
    {
        $this->logDebug("Map entity to output dto for Security attribute");
        $span = $this->startSpan("Map entity to output dto for Security attribute");
        try {
            $trustedClient = $value->getTrustedClient();
            $relyingParty = $value->getRelyingParty();
            $dto = new SecurityAttributeApiDTO();
            $dto->uid = $value->getUid();
            $dto->trustedClient = $trustedClient ? ['$ref' => $trustedClient->uid()] : null;
            $dto->relyingParty = $relyingParty ? ['$ref' => $relyingParty->uid()] : null;
            $dto->resource = $value->getResource();
            $dto->attribute = $value->getAttribute();
            $dto->enabled = $value->getEnabled();
            $dto->readVisibility = $value->getReadVisibility();
            $dto->writeVisibility = $value->getWriteVisibility();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
