<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Retrieve\SecurityAttributeRetrieveUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Retrieve\SecurityAttributeRetrieveResult;

class SecurityAttributeRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityAttributeRetrieveUsecase $retrieveUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/security-attributes{uid}",
        tags: ["Security attribute"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityAttributeApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve Security attribute");
        $span = $this->startSpan("Retrieve Security attribute");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapSecurityAttribute($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapSecurityAttribute(SecurityAttributeRetrieveResult $value): SecurityAttributeApiDTO
    {
        $this->logDebug("Map entity to output dto for Security attribute");
        $span = $this->startSpan("Map entity to output dto for Security attribute");
        try {
            $trustedClient = $value->getTrustedClient();
            $relyingParty = $value->getRelyingParty();
            $dto = new SecurityAttributeApiDTO();
            $dto->uid = $value->getUid();
            $dto->trustedClient = $trustedClient ? ['$ref' => $trustedClient->uid()] : null;
            $dto->relyingParty = $relyingParty ? ['$ref' => $relyingParty->uid()] : null;
            $dto->resource = $value->getResource();
            $dto->attribute = $value->getAttribute();
            $dto->enabled = $value->getEnabled();
            $dto->readVisibility = $value->getReadVisibility();
            $dto->writeVisibility = $value->getWriteVisibility();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
