<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Update\SecurityAttributeUpdateUsecase;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeUidVO;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeTrustedClientVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeRelyingPartyVO;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeResourceVO;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeAttributeVO;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeEnabledVO;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeReadVisibilityVO;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeWriteVisibilityVO;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Update\SecurityAttributeUpdateParams;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Update\SecurityAttributeUpdateResult;

class SecurityAttributeUpdateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly SecurityAttributeUpdateUsecase $updateUsecase,
    ) {
    }
    #[OA\Put(
        path: "/api/access/security-attributes/{uid}",
        tags: ["Security attribute"],
        description: "Update the value",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityAttributeApiDTO")),
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityAttributeApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
        new OA\Response(response: 409, description: "Optimist lock"),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function update(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Update Security attribute");
        $span = $this->startSpan("Update Security attribute");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $value = $this->readSecurityAttribute($request);
            $result = $this->updateUsecase->update($uid, $value);
            $this->sql->commit();
            $value = $this->mapSecurityAttribute($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }

    private function readSecurityAttribute(ServerRequestInterface $request): SecurityAttributeUpdateParams
    {
        $this->logDebug("Read entity for Security attribute");
        $span = $this->startSpan("Read entity for Security attribute");
        try {
            $body = $request->getParsedBody();
            $errorsList = new ConstraintFailList();
            $value = new SecurityAttributeUpdateParams();
            $value->uid(SecurityAttributeUidVO::tryFrom($body['uid'] ?? null, $errorsList));
            $trustedClient = $body['trustedClient'] ?? null;
            if ($trustedClient && isset($trustedClient['$ref'])) {
                $value->trustedClient(SecurityAttributeTrustedClientVO::tryFrom(new TrustedClientRef(uid: $trustedClient['$ref']), $errorsList));
            }
            $relyingParty = $body['relyingParty'] ?? null;
            if ($relyingParty && isset($relyingParty['$ref'])) {
                $value->relyingParty(SecurityAttributeRelyingPartyVO::tryFrom(new RelyingPartyRef(uid: $relyingParty['$ref']), $errorsList));
            }
            $value->resource(SecurityAttributeResourceVO::tryFrom($body['resource'] ?? null, $errorsList));
            $value->attribute(SecurityAttributeAttributeVO::tryFrom($body['attribute'] ?? null, $errorsList));
            $value->enabled(SecurityAttributeEnabledVO::tryFrom($body['enabled'] ?? null, $errorsList));
            $value->readVisibility(SecurityAttributeReadVisibilityVO::tryFrom($body['readVisibility'] ?? null, $errorsList));
            $value->writeVisibility(SecurityAttributeWriteVisibilityVO::tryFrom($body['writeVisibility'] ?? null, $errorsList));
            $value->version(SecurityAttributeVersionVO::tryFrom($body['version'] ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function mapSecurityAttribute(SecurityAttributeUpdateResult $value): SecurityAttributeApiDTO
    {
        $this->logDebug("Map entity to output dto for Security attribute");
        $span = $this->startSpan("Map entity to output dto for Security attribute");
        try {
            $trustedClient = $value->getTrustedClient();
            $relyingParty = $value->getRelyingParty();
            $dto = new SecurityAttributeApiDTO();
            $dto->uid = $value->getUid();
            $dto->trustedClient = $trustedClient ? ['$ref' => $trustedClient->uid()] : null;
            $dto->relyingParty = $relyingParty ? ['$ref' => $relyingParty->uid()] : null;
            $dto->resource = $value->getResource();
            $dto->attribute = $value->getAttribute();
            $dto->enabled = $value->getEnabled();
            $dto->readVisibility = $value->getReadVisibility();
            $dto->writeVisibility = $value->getWriteVisibility();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
