<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver;

use Override;
use Slim\Routing\RouteCollectorProxy;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest\SecurityAttributeAllowController;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest\SecurityAttributeListController;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest\SecurityAttributeCreateController;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest\SecurityAttributeRetrieveController;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest\SecurityAttributeUpdateController;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest\SecurityAttributeDeleteController;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest\SecurityAttributeDisableController;
use Civi\Lughauth\Features\Access\SecurityAttribute\Infrastructure\Driver\Rest\SecurityAttributeEnableController;
use Civi\Lughauth\Shared\Infrastructure\MicroPlugin;
use Civi\Lughauth\Shared\Event\EventListenersRegistrarInterface;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Policy\Allow\Create\IsAutenticatedCreateAllow;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Create\SecurityAttributeCreateAllowDecision;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Policy\Allow\Update\IsAutenticatedUpdateAllow;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Update\SecurityAttributeUpdateAllowDecision;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Policy\Allow\Retrieve\IsAutenticatedRetrieveAllow;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Retrieve\SecurityAttributeRetrieveAllowDecision;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Policy\Allow\List\IsAutenticatedListAllow;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\List\SecurityAttributeListAllowDecision;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Policy\Allow\Delete\IsAutenticatedDeleteAllow;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Delete\SecurityAttributeDeleteAllowDecision;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Policy\Allow\Disable\IsAutenticatedDisableAllow;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Disable\SecurityAttributeDisableAllowDecision;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Policy\Allow\Enable\IsAutenticatedEnableAllow;
use Civi\Lughauth\Features\Access\SecurityAttribute\Application\Usecase\Enable\SecurityAttributeEnableAllowDecision;

class SecurityAttributePlugin extends MicroPlugin
{
    #[Override]
    public function registerRoutes(RouteCollectorProxy $app)
    {
        $app->group('/api/access/security-attributes', [$this, 'setRoutesForSecurityAttribute']);
        $app->group('/api/me/acl/access/security-attributes', [$this, 'setRoutesForSecurityAttributeAcl']);
    }
    #[Override]
    public function registerEvents(EventListenersRegistrarInterface $bus)
    {
        $bus->registerListener(SecurityAttributeCreateAllowDecision::class, IsAutenticatedCreateAllow::class);
        $bus->registerListener(SecurityAttributeUpdateAllowDecision::class, IsAutenticatedUpdateAllow::class);
        $bus->registerListener(SecurityAttributeRetrieveAllowDecision::class, IsAutenticatedRetrieveAllow::class);
        $bus->registerListener(SecurityAttributeListAllowDecision::class, IsAutenticatedListAllow::class);
        $bus->registerListener(SecurityAttributeDeleteAllowDecision::class, IsAutenticatedDeleteAllow::class);
        $bus->registerListener(SecurityAttributeDisableAllowDecision::class, IsAutenticatedDisableAllow::class);
        $bus->registerListener(SecurityAttributeEnableAllowDecision::class, IsAutenticatedEnableAllow::class);
    }
    public function setRoutesForSecurityAttributeAcl(RouteCollectorProxy $securityAttributeGroup)
    {
        $securityAttributeGroup->get('', [SecurityAttributeAllowController::class, 'genericAllow']);
        $securityAttributeGroup->get('/{uid}', [SecurityAttributeAllowController::class, 'contextualAllow']);
    }
    public function setRoutesForSecurityAttribute(RouteCollectorProxy $securityAttributeGroup)
    {
        $securityAttributeGroup->get('', [SecurityAttributeListController::class, 'list']);
        $securityAttributeGroup->post('', [SecurityAttributeCreateController::class, 'create']);
        $securityAttributeGroup->get('/{uid}', [SecurityAttributeRetrieveController::class, 'retrieve']);
        $securityAttributeGroup->put('/{uid}', [SecurityAttributeUpdateController::class, 'update']);
        $securityAttributeGroup->delete('/{uid}', [SecurityAttributeDeleteController::class, 'delete']);
        $securityAttributeGroup->delete('', [SecurityAttributeDeleteController::class, 'deleteAllForQuery']);
        $securityAttributeGroup->get('/~/delete-status/{uid}', [SecurityAttributeDeleteController::class, 'checkDeleteAllForQueryStatus']);
        $securityAttributeGroup->patch('/~/disable', [SecurityAttributeDisableController::class, 'disableAllForQuery']);
        $securityAttributeGroup->patch('/{uid}/disable', [SecurityAttributeDisableController::class, 'disable']);
        $securityAttributeGroup->get('/~/disable-status/{uid}', [SecurityAttributeDisableController::class, 'checkDisableAllForQueryStatus']);
        $securityAttributeGroup->patch('/~/enable', [SecurityAttributeEnableController::class, 'enableAllForQuery']);
        $securityAttributeGroup->patch('/{uid}/enable', [SecurityAttributeEnableController::class, 'enable']);
        $securityAttributeGroup->get('/~/enable-status/{uid}', [SecurityAttributeEnableController::class, 'checkEnableAllForQueryStatus']);
    }
}
