<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Application\Policy\Allow\Delete;

use Throwable;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Usecase\Delete\SecurityDomainDeleteAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class IsAutenticatedDeleteAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(SecurityDomainDeleteAllowDecision $proposal): SecurityDomainDeleteAllowDecision
    {
        $this->logDebug("Check IsAutenticatedDeleteAllow Security domain");
        $span = $this->startSpan("Check IsAutenticatedDeleteAllow Security domain");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->allow->allowed) {
                $belongs = !$userContext->anonimous;
                $allowed = $belongs ? Allow::allowed('delete', 'Enabled for user IsAutenticatedDelete') : Allow::disallowed('delete', 'Disabled if not IsAutenticatedDelete');
                $proposal->allow = $allowed;
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
