<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainAttributes;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomain;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainFilter;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainCursor;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainReadGateway;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainSlide;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityDomainVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityDomainReadGateway $readGateway,
        private readonly SecurityDomainWriteGateway $writeGateway
    ) {
    }

    public function prepareVisibleData(SecurityDomain $content): SecurityDomainAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Security domain");
        $span = $this->startSpan("Prepare hidratation to visible data for Security domain");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new SecurityDomainExposeProposal($content, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function copyWithFixed(SecurityDomainAttributes $attributes, ?SecurityDomain $original = null): SecurityDomainAttributes
    {
        $this->logDebug("Copy with fixed for Security domain");
        $span = $this->startSpan("Copy with fixed for  Security domain");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new SecurityDomainPresetProposal($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(SecurityDomainAttributes $attributes, ?SecurityDomain $original = null): SecurityDomainAttributes
    {
        $this->logDebug("Copy with hidden for Security domain");
        $span = $this->startSpan("Copy with hidden for  Security domain");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|SecurityDomainRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Security domain");
        $span = $this->startSpan("Check visibility of an iterator for  Security domain");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new SecurityDomainFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, SecurityDomainRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new SecurityDomainRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(SecurityDomainFilter $filter, ?SecurityDomainCursor $cursor = null): SecurityDomainSlide
    {
        $this->logDebug("List visibles for Security domain");
        $span = $this->startSpan("List visibles for  Security domain");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(SecurityDomainFilter $filter): int
    {
        $this->logDebug("Count visibles for Security domain");
        $span = $this->startSpan("Count visibles for  Security domain");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(SecurityDomainFilter $filter, ?SecurityDomainCursor $cursor = null): SecurityDomainSlide
    {
        $this->logDebug("List visibles for update for Security domain");
        $span = $this->startSpan("List visibles for update for  Security domain");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(SecurityDomainFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Security domain");
        $span = $this->startSpan("Count visibles for update for  Security domain");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(SecurityDomainRef $ref): ?SecurityDomain
    {
        $this->logDebug("Retrieve visible for Security domain");
        $span = $this->startSpan("Retrieve visible for  Security domain");
        try {
            $filter = new SecurityDomainFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(SecurityDomainRef $ref): ?SecurityDomain
    {
        $this->logDebug("Retrieve visible for update for Security domain");
        $span = $this->startSpan("Retrieve visible for update for  Security domain");
        try {
            $filter = new SecurityDomainFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?SecurityDomainRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Security domain");
        $span = $this->startSpan("Check fields for fix for Security domain");
        try {
            $result = $this->dispacher->dispatch(new SecurityDomainFixedFieldsProposal(SecurityDomain::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?SecurityDomainRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Security domain");
        $span = $this->startSpan("Check fields to hide for  Security domain");
        try {
            $result = $this->dispacher->dispatch(new SecurityDomainHideFieldsProposal([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(SecurityDomainAttributes $attributes): SecurityDomainAttributes
    {
        $this->logDebug("Check visibility of parent references for Security domain");
        $span = $this->startSpan("Check visibility of parent references for  Security domain");
        try {
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(SecurityDomainFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Security domain");
        $span = $this->startSpan("Compose visibility filter for  Security domain");
        try {
            $result = $this->dispacher->dispatch(new SecurityDomainFilterProposal($filter));
            return $result->securityDomainFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(SecurityDomain $value): bool
    {
        $this->logDebug("Check if item is visible for Security domain");
        $span = $this->startSpan("Check if item is visible for  Security domain");
        try {
            $result = $this->dispacher->dispatch(new SecurityDomainVisibilityProposal(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
