<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Application\Usecase\Disable;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Service\Visibility\SecurityDomainVisibilityService;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityDomainDisableUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityDomainVisibilityService $visibility,
        private readonly SecurityDomainWriteGateway $writer
    ) {
    }

    public function allowDisable(?SecurityDomainRef $ref = null): Allow
    {
        $this->logDebug("Check allow of Disable usecase for Security domain");
        $span = $this->startSpan("Check allow of Disable usecase for Security domain");
        try {
            $result = $this->dispacher->dispatch(new SecurityDomainDisableAllowDecision(Allow::allowed('disable', 'Allowed to Security domain by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function disable(string $uid): SecurityDomainDisableResult
    {
        $this->logDebug("Run Disable usecase for Security domain");
        $span = $this->startSpan("Run Disable usecase for Security domain");
        try {
            $ref = new SecurityDomainRef($uid);
            $allow = $this->allowDisable($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new SecurityDomainDisableCheck($original));
            $modified = $original->disable();
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new SecurityDomainDisableResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
