<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Service\Visibility\SecurityDomainVisibilityService;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainReadGateway;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainFilter;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainCursor;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityDomainListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityDomainVisibilityService $visibility,
        private readonly SecurityDomainReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Security domain");
        $span = $this->startSpan("Check allow of list usecase for Security domain");
        try {
            $result = $this->dispacher->dispatch(new SecurityDomainListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(SecurityDomainFilter $filter): int
    {
        $this->logDebug("Run list usecase for Security domain");
        $span = $this->startSpan("Run list usecase for Security domain");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new SecurityDomainListInputProposal($filter, null));
            return $this->visibility->countVisibles($input->filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(SecurityDomainFilter $filter, ?SecurityDomainCursor $cursor = null): SecurityDomainAttributesSlide
    {
        $this->logDebug("Run list usecase for Security domain");
        $span = $this->startSpan("Run list usecase for Security domain");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new SecurityDomainListInputProposal($filter, $cursor));
            $slide = $this->visibility->listVisibles($input->filter, $input->cursor);
            $output = new SecurityDomainAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($item->toAttributes()), $slide->values()));
            return $this->dispacher->dispatch(new SecurityDomainListOutputProposal($output))->slide;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
