<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Service\Visibility\SecurityDomainVisibilityService;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityDomainUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityDomainVisibilityService $visibility,
        private readonly SecurityDomainWriteGateway $writer
    ) {
    }

    public function allowUpdate(?SecurityDomainRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Security domain");
        $span = $this->startSpan("Check allow update usecase for Security domain");
        try {
            $result = $this->dispacher->dispatch(new SecurityDomainUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?SecurityDomainRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Security domain");
        $span = $this->startSpan("Check fixed fields for update usecase for Security domain");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, SecurityDomainUpdateParams $params): SecurityDomainUpdateResult
    {
        $this->logDebug("Run update usecase for Security domain");
        $span = $this->startSpan("Run update usecase for Security domain");
        try {
            $ref = new SecurityDomainRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new SecurityDomainUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new SecurityDomainUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new SecurityDomainUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
