<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomain;
use Civi\Lughauth\Features\Access\SecurityDomain\Infrastructure\Driven\SecurityDomainReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;

class SecurityDomainReadGateway
{
    private SecurityDomainReadRepository $repository;
    public function __construct(ContainerInterface $container, ?SecurityDomainReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(SecurityDomainReadRepositoryAdapter::class);
    }
    public function enrich(SecurityDomainRef $ref): ?SecurityDomain
    {
        return $this->repository->enrich($ref);
    }
    public function list(?SecurityDomainFilter $filter = null, ?SecurityDomainCursor $cursor = null): SecurityDomainSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(SecurityDomainFilter $filter): ?SecurityDomain
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?SecurityDomainFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?SecurityDomainFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?SecurityDomain
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByName(string $name): ?SecurityDomain
    {
        return $this->repository->findOneByName($name);
    }
}
