<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomain;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityDomain\Infrastructure\Driven\SecurityDomainWriteRepositoryAdapter;

class SecurityDomainWriteGateway
{
    private SecurityDomainWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?SecurityDomainWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(SecurityDomainWriteRepositoryAdapter::class);
    }
    public function create(SecurityDomain $entity, ?Closure $verify = null): SecurityDomain
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(SecurityDomainRef $ref, SecurityDomain $entity): SecurityDomain
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(SecurityDomain $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?SecurityDomainFilter $filter = null, ?SecurityDomainCursor $cursor = null): SecurityDomainSlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(SecurityDomainFilter $filter): ?SecurityDomain
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?SecurityDomainFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?SecurityDomainFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
    public function findOneForUpdateByUid(string $uid): ?SecurityDomain
    {
        return $this->repository->findOneForUpdateByUid($uid);
    }
    public function findOneForUpdateByName(string $name): ?SecurityDomain
    {
        return $this->repository->findOneForUpdateByName($name);
    }
}
