<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainLevelVO;

trait SecurityDomainLevelAccesor
{
    private SecurityDomainLevelVO $_level;
    public function getLevel(): int
    {
        return $this->_level->value();
    }
    public function level(): SecurityDomainLevelVO
    {
        return $this->_level;
    }
    public function isLevelChanged(?self $original = null): bool
    {
        return $this->_level->value() !== ($original ? $original->_level->value() : null);
    }
    public function withLevel(SecurityDomainLevelVO|int $level): self
    {
        $copy = clone $this;
        $copy->_level = SecurityDomainLevelVO::from($level);
        return $copy;
    }
}
