<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainNameVO;

trait SecurityDomainNameAccesor
{
    private SecurityDomainNameVO $_name;
    public function getName(): string
    {
        return $this->_name->value();
    }
    public function name(): SecurityDomainNameVO
    {
        return $this->_name;
    }
    public function isNameChanged(?self $original = null): bool
    {
        return $this->_name->value() !== ($original ? $original->_name->value() : null);
    }
    public function withName(SecurityDomainNameVO|string $name): self
    {
        $copy = clone $this;
        $copy->_name = SecurityDomainNameVO::from($name);
        return $copy;
    }
}
