namespace Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class SecurityDomainLevelVO
{
    public static function from(SecurityDomainLevelVO|int $value): SecurityDomainLevelVO
    {
        if (is_a($value, SecurityDomainLevelVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?SecurityDomainLevelVO
    {
        if (is_a($value, SecurityDomainLevelVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (is_integer($value)) {
            return new SecurityDomainLevelVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['level'], [$value], ['int']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly int $level
    ) {
    }
    public function value(): int
    {
        return $this->level;
    }
}
