<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainReadRepository;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainSlide;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainFilter;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainCursor;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomain;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityDomainReadRepositoryAdapter implements SecurityDomainReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityDomainPdoConnector $conn,
    ) {
    }
    #[Override]
    public function resolve(SecurityDomainRef $ref): ?SecurityDomain
    {
        if ($ref instanceof SecurityDomain) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new SecurityDomainFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?SecurityDomainFilter $filter = null, ?SecurityDomainCursor $sort = null): SecurityDomainSlide
    {
        $this->logDebug("List for Security domain on adapter ");
        $span = $this->startSpan("List for Security domain on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new SecurityDomainSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new SecurityDomainCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?SecurityDomainFilter $filter): ?SecurityDomain
    {
        $this->logDebug("Retrieve for Security domain on adapter ");
        $span = $this->startSpan("Retrieve for Security domain on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?SecurityDomainFilter $filter): bool
    {
        $this->logDebug("Exists for Security domain on adapter ");
        $span = $this->startSpan("Exists for Security domain on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?SecurityDomainFilter $filter = null): int
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?SecurityDomain
    {
        $this->logDebug("Find on by uid for Security domain on adapter");
        $span = $this->startSpan("Find on by uid for Security domain on adapter");
        try {
            return $this->conn->retrieve(new SecurityDomainFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByName(string $name): ?SecurityDomain
    {
        $this->logDebug("Find on by name for Security domain on adapter");
        $span = $this->startSpan("Find on by name for Security domain on adapter");
        try {
            return $this->conn->retrieve(new SecurityDomainFilter(name: $name));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
