<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainWriteRepository;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainSlide;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainCursor;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainFilter;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomain;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityDomainWriteRepositoryAdapter implements SecurityDomainWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityDomainPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
    ) {
    }
    #[Override]
    public function listForUpdate(?SecurityDomainFilter $filter = null, ?SecurityDomainCursor $sort = null): SecurityDomainSlide
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new SecurityDomainSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new SecurityDomainCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(SecurityDomainFilter $filter): ?SecurityDomain
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?SecurityDomainFilter $filter): bool
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?SecurityDomainFilter $filter = null): int
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(SecurityDomain $entity, ?Closure $verify = null): SecurityDomain
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(SecurityDomainRef $reference, SecurityDomain $entity): SecurityDomain
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(SecurityDomain $entity): bool
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            $result = $this->conn->delete($entity);
