    {
        $this->logDebug("Find on by name for Security domain on adapter");
        $span = $this->startSpan("Find on by name for Security domain on adapter");
        try {
            return $this->conn->retrieveForUpdate(new SecurityDomainFilter(name: $name));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(SecurityDomain $entity)
    {
        $this->logDebug("Count for Security domain on adapter ");
        $span = $this->startSpan("Count for Security domain on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
