use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Usecase\Delete\SecurityDomainDeleteUsecase;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainFilter;
use Civi\Lughauth\Features\Access\SecurityDomain\Infrastructure\Driver\Batch\SecurityDomainTaskDelete;

class SecurityDomainDeleteController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly Runner $runner,
        private readonly SecurityDomainDeleteUsecase $deleteUsecase,
    ) {
    }
    #[OA\Delete(
        path: "/api/access/security-domains/{uid}",
        tags: ["Security domain"],
        description: "Delete the record",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityDomainApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function delete(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Delete Security domain");
        $span = $this->startSpan("Delete Security domain");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $this->deleteUsecase->delete($uid);
            $this->sql->commit();
            return $response->withStatus(204)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            ;
            $span->end();
        }
    }
    public function deleteAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Delete all for query Security domain");
        $span = $this->startSpan("Delete all for query Security domain");
        try {
            $params = $request->getQueryParams();
            $filter = new SecurityDomainFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                name: $params['name'] ?? null,
                enabled: $params['enabled'] ?? null,
            );
            $res = $this->runner->run(SecurityDomainTaskDelete::class, ['filter' => $filter]);
            $response->getBody()->write(json_encode($res));
            return $response;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function checkDeleteAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check delete all for query Security domain");
        $span = $this->startSpan("Check delete all for query Security domain");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $info = $this->runner->detail(new TaskKey($uid));
            if (!$info) {
                throw new NotFoundException('-');
            }
            $response->getBody()->write(json_encode($info));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
