<?php

# @autogenerated
declare(strict_types=1);
namespace Civi\Lughauth\Features\Access\SecurityDomain\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;

use OpenApi\Attributes as OA;
use Throwable;

use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Usecase\Disable\SecurityDomainDisableUsecase;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainFilter;
use Civi\Lughauth\Features\Access\SecurityDomain\Infrastructure\Driver\Batch\SecurityDomainTaskDisable;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainUidVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainNameVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainLevelVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainReadAllVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainWriteAllVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainManageAllVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainEnabledVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\ValueObject\SecurityDomainVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Usecase\Disable\SecurityDomainDisableResult;

class SecurityDomainDisableController
{
  use LoggerAwareTrait;
  use TracerAwareTrait;

  public function __construct(
    private readonly Runner $runner,
    private readonly SqlTemplate $sql,
    private readonly SecurityDomainDisableUsecase $disableUsecase,
  ) {
  }
  #[OA\Patch(path: "/api/access/security-domains/{uid}/disable", tags: ["Security domain"], description: "Update the value",
    parameters: [
      new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
    responses: [
      new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityDomainApiDTO")),
      new OA\Response(response: 404, description: "Not found"),
    ])
  ]
  public function disable(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("disable for Security domain");
    $span = $this->startSpan("disable for Security domain");
    $this->sql->begin();
    try {
      if (!isset($args['uid'])) {
        throw new NotFoundException('-');
      }
      $uid = $args['uid'];
      $result = $this->disableUsecase->disable($uid);
      $this->sql->commit();
      $value = $this->mapSecurityDomain($result);
      $response->getBody()->write(json_encode($value));
      return $response->withStatus(201)
            ->withHeader('Content-Type', 'application/json');
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $this->sql->close();
      $span->end();
    }
  }
  public function disableAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("disable all for query Security domain");
    $span = $this->startSpan("disable all for query Security domain");
    try {
      $params = $request->getQueryParams();
      $filter = new SecurityDomainFilter(
        uids: isset( $params['uid'] ) ? [$params['uid']] : ( isset($params['uids']) ? explode(',', $params['uids']) : null),
        search: $params['search'] ?? null,
        name: $params['name'] ?? null,
        enabled: $params['enabled'] ?? null,
      );
      $res = $this->runner->run(SecurityDomainTaskDisable::class, ['filter' => $filter]);
      $response->getBody()->write( json_encode($res) );      return $response;
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
  public function checkDisableAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("Check disable all for query Security domain");
    $span = $this->startSpan("Check disable all for query Security domain");
    try {
      if (!isset($args['uid'])) {
        throw new NotFoundException('-');
      }
      $uid = $args['uid'];
      $info = $this->runner->detail( new TaskKey($uid) );
      if( !$info ) {
        throw new NotFoundException('-');
      }
      $response->getBody()->write(json_encode($info));
      return $response->withStatus(200)
        ->withHeader('Content-Type', 'application/json');
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
}

  private function mapSecurityDomain(SecurityDomainDisableResult $value): SecurityDomainApiDTO
  {
    $this->logDebug("Map entity to output dto for Security domain");
    $span = $this->startSpan("Map entity to output dto for Security domain");
    try {
      $dto = new SecurityDomainApiDTO();
      $dto->uid = $value->getUid();
      $dto->name = $value->getName();
      $dto->level = $value->getLevel();
      $dto->readAll = $value->getReadAll();
      $dto->writeAll = $value->getWriteAll();
      $dto->manageAll = $value->getManageAll();
      $dto->enabled = $value->getEnabled();
      $dto->version = $value->getVersion();
      return $dto;
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
