<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityDomain\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainFilter;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\Gateway\SecurityDomainCursor;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Usecase\List\SecurityDomainListUsecase;

class SecurityDomainListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SecurityDomainListUsecase $listUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/security-domains",
        tags: ["Security domain"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/SecurityDomainApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Security domain");
        $span = $this->startSpan("List Security domain");
        try {
            $params = $request->getQueryParams();
            $filter = new SecurityDomainFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                name: $params['name'] ?? null,
                enabled: $params['enabled'] ?? null,
            );
            $cursor = new SecurityDomainCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
                sinceName: $params['since-name'] ?? null,
                order: isset($params['order']) ? $this->extractOrder(explode(',', $params['order'])) : null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapSecurityDomain($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function extractOrder(array $order): array
    {
        $this->logDebug("Extract order for Security domain");
        $span = $this->startSpan("Extract order for Security domain");
        try {
            $mapped = [];
            foreach ($order as $ord) {
                if ($ord === 'name-asc') {
                    $mapped[] = 'nameAsc';
                }
                if ($ord === 'name-desc') {
                    $mapped[] = 'nameDesc';
                }
            }
            return $mapped;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?SecurityDomainCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Security domain");
        $span = $this->startSpan("Generic allow for Security domain");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            if ($value && strpos('NAME-ASC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            if ($value && strpos('NAME-DESC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapSecurityDomain(SecurityDomainAttributes $value): SecurityDomainApiDTO
    {
        $this->logDebug("Map entity to output dto for Security domain");
        $span = $this->startSpan("Map entity to output dto for Security domain");
        try {
            $dto = new SecurityDomainApiDTO();
            $dto->uid = $value->getUid();
            $dto->name = $value->getName();
            $dto->level = $value->getLevel();
            $dto->readAll = $value->getReadAll();
            $dto->writeAll = $value->getWriteAll();
            $dto->manageAll = $value->getManageAll();
            $dto->enabled = $value->getEnabled();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
