<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Application\Policy\Allow\List;

use Throwable;
use Civi\Lughauth\Features\Access\SecurityScope\Application\Usecase\List\SecurityScopeListAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class IsAutenticatedListAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(SecurityScopeListAllowDecision $proposal): SecurityScopeListAllowDecision
    {
        $this->logDebug("Check IsAutenticatedListAllow Security scope");
        $span = $this->startSpan("Check IsAutenticatedListAllow Security scope");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->allow->allowed) {
                $belongs = !$userContext->anonimous;
                $allowed = $belongs ? Allow::allowed('list', 'Enabled for user IsAutenticatedList') : Allow::disallowed('list', 'Disabled if not IsAutenticatedList');
                $proposal->allow = $allowed;
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
