<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Service\Visibility\TrustedClientVisibilityService;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Service\Visibility\RelyingPartyVisibilityService;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeAttributes;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScope;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeFilter;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeCursor;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeReadGateway;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeSlide;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityScopeVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityScopeReadGateway $readGateway,
        private readonly SecurityScopeWriteGateway $writeGateway,
        private readonly TrustedClientVisibilityService $trustedClientVisibilityService,
        private readonly RelyingPartyVisibilityService $relyingPartyVisibilityService
    ) {
    }

    public function prepareVisibleDataInList(SecurityScope $content): SecurityScopeAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(SecurityScope $content): SecurityScopeAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(SecurityScopeAttributes $attributes, ?SecurityScope $original = null): SecurityScopeAttributes
    {
        $this->logDebug("Copy with fixed for Security scope");
        $span = $this->startSpan("Copy with fixed for  Security scope");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new SecurityScopePresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(SecurityScopeAttributes $attributes, ?SecurityScope $original = null): SecurityScopeAttributes
    {
        $this->logDebug("Copy with hidden for Security scope");
        $span = $this->startSpan("Copy with hidden for  Security scope");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|SecurityScopeRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Security scope");
        $span = $this->startSpan("Check visibility of an iterator for  Security scope");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new SecurityScopeFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, SecurityScopeRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new SecurityScopeRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(SecurityScopeFilter $filter, ?SecurityScopeCursor $cursor = null): SecurityScopeSlide
    {
        $this->logDebug("List visibles for Security scope");
        $span = $this->startSpan("List visibles for  Security scope");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(SecurityScopeFilter $filter): int
    {
        $this->logDebug("Count visibles for Security scope");
        $span = $this->startSpan("Count visibles for  Security scope");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(SecurityScopeFilter $filter, ?SecurityScopeCursor $cursor = null): SecurityScopeSlide
    {
        $this->logDebug("List visibles for update for Security scope");
        $span = $this->startSpan("List visibles for update for  Security scope");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(SecurityScopeFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Security scope");
        $span = $this->startSpan("Count visibles for update for  Security scope");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(SecurityScopeRef $ref): ?SecurityScope
    {
        $this->logDebug("Retrieve visible for Security scope");
        $span = $this->startSpan("Retrieve visible for  Security scope");
        try {
            $filter = new SecurityScopeFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(SecurityScopeRef $ref): ?SecurityScope
    {
        $this->logDebug("Retrieve visible for update for Security scope");
        $span = $this->startSpan("Retrieve visible for update for  Security scope");
        try {
            $filter = new SecurityScopeFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?SecurityScopeRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Security scope");
        $span = $this->startSpan("Check fields for fix for Security scope");
        try {
            $result = $this->dispacher->dispatch(new SecurityScopeCollectNonEditableFields(SecurityScope::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?SecurityScopeRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Security scope");
        $span = $this->startSpan("Check fields to hide for  Security scope");
        try {
            $result = $this->dispacher->dispatch(new SecurityScopeCollectNonVisibleFields([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(SecurityScopeAttributes $attributes): SecurityScopeAttributes
    {
        $this->logDebug("Check visibility of parent references for Security scope");
        $span = $this->startSpan("Check visibility of parent references for  Security scope");
        try {
            if ($attributes->getTrustedClient() && !$this->trustedClientVisibilityService->checkVisibility($attributes->getTrustedClient())) {
                throw new NotFoundException("Unknow TrustedClient " . $attributes->getTrustedClient());
            }
            if ($attributes->getRelyingParty() && !$this->relyingPartyVisibilityService->checkVisibility($attributes->getRelyingParty())) {
                throw new NotFoundException("Unknow RelyingParty " . $attributes->getRelyingParty());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(SecurityScopeFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Security scope");
        $span = $this->startSpan("Compose visibility filter for  Security scope");
        try {
            $result = $this->dispacher->dispatch(new SecurityScopeRestrictFilterToVisibility($filter));
            return $result->securityScopeFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(SecurityScope $value): bool
    {
        $this->logDebug("Check if item is visible for Security scope");
        $span = $this->startSpan("Check if item is visible for  Security scope");
        try {
            $result = $this->dispacher->dispatch(new SecurityScopeVisibilityCheck(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(SecurityScope $content, bool $inlist): SecurityScopeAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Security scope");
        $span = $this->startSpan("Prepare hidratation to visible data for Security scope");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new SecurityScopeEnrichForView($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
