<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Application\Usecase\Update;

use RuntimeException;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScope;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class SecurityScopeUpdateCheck
{
    public function __construct(public readonly SecurityScopeUpdateParams $input, public readonly SecurityScope $current)
    {
    }
    public function reject(RuntimeException|ConstraintFail $fail)
    {
        if ($fail instanceof ConstraintFail) {
            throw ConstraintException::ofFail($fail);
        } else {
            throw $fail;
        }
    }
}
