<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;
use Civi\Lughauth\Features\Access\SecurityScope\Application\Service\Visibility\SecurityScopeVisibilityService;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway\SecurityScopeWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class SecurityScopeUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly SecurityScopeVisibilityService $visibility,
        private readonly SecurityScopeWriteGateway $writer
    ) {
    }

    public function allowUpdate(?SecurityScopeRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Security scope");
        $span = $this->startSpan("Check allow update usecase for Security scope");
        try {
            $result = $this->dispacher->dispatch(new SecurityScopeUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?SecurityScopeRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Security scope");
        $span = $this->startSpan("Check fixed fields for update usecase for Security scope");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, SecurityScopeUpdateParams $params): SecurityScopeUpdateResult
    {
        $this->logDebug("Run update usecase for Security scope");
        $span = $this->startSpan("Run update usecase for Security scope");
        try {
            $ref = new SecurityScopeRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new SecurityScopeUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new SecurityScopeUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new SecurityScopeUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
