<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway;

use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScope;

class SecurityScopeCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(SecurityScope $securityScope, ?int $size): SecurityScopeCursor
    {
        $next = clone $this;
        $next->sinceUid = $securityScope->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
