<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScope;
use Civi\Lughauth\Features\Access\SecurityScope\Infrastructure\Driven\SecurityScopeReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;

class SecurityScopeReadGateway
{
    private SecurityScopeReadRepository $repository;
    public function __construct(ContainerInterface $container, ?SecurityScopeReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(SecurityScopeReadRepositoryAdapter::class);
    }
    public function enrich(SecurityScopeRef $ref): ?SecurityScope
    {
        return $this->repository->enrich($ref);
    }
    public function list(?SecurityScopeFilter $filter = null, ?SecurityScopeCursor $cursor = null): SecurityScopeSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(SecurityScopeFilter $filter): ?SecurityScope
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?SecurityScopeFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?SecurityScopeFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?SecurityScope
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByResourceAndScope(string $resource, string $scope): ?SecurityScope
    {
        return $this->repository->findOneByResourceAndScope($resource, $scope);
    }
}
