<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScope;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;
use Civi\Lughauth\Features\Access\SecurityScope\Infrastructure\Driven\SecurityScopeWriteRepositoryAdapter;

class SecurityScopeWriteGateway
{
    private SecurityScopeWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?SecurityScopeWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(SecurityScopeWriteRepositoryAdapter::class);
    }
    public function create(SecurityScope $entity, ?Closure $verify = null): SecurityScope
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(SecurityScopeRef $ref, SecurityScope $entity): SecurityScope
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(SecurityScope $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?SecurityScopeFilter $filter = null, ?SecurityScopeCursor $cursor = null): SecurityScopeSlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(SecurityScopeFilter $filter): ?SecurityScope
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?SecurityScopeFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?SecurityScopeFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
    public function findOneForUpdateByUid(string $uid): ?SecurityScope
    {
        return $this->repository->findOneForUpdateByUid($uid);
    }
    public function findOneForUpdateByResourceAndScope(string $resource, string $scope): ?SecurityScope
    {
        return $this->repository->findOneForUpdateByResourceAndScope($resource, $scope);
    }
}
