<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeRelyingPartyVO;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;

trait SecurityScopeRelyingPartyAccesor
{
    private ?SecurityScopeRelyingPartyVO $_relyingParty = null;
    public function getRelyingParty(): ?RelyingPartyRef
    {
        return $this->_relyingParty?->value();
    }
    public function relyingParty(): ?SecurityScopeRelyingPartyVO
    {
        return $this->_relyingParty;
    }
    public function isRelyingPartyChanged(?self $original = null): bool
    {
        return $this->_relyingParty->value() !== ($original ? $original->_relyingParty->value() : null);
    }
    public function withRelyingParty(SecurityScopeRelyingPartyVO|RelyingPartyRef|null $relyingParty): self
    {
        $copy = clone $this;
        $copy->_relyingParty = SecurityScopeRelyingPartyVO::from($relyingParty);
        return $copy;
    }
}
