<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeResourceVO;

trait SecurityScopeResourceAccesor
{
    private SecurityScopeResourceVO $_resource;
    public function getResource(): string
    {
        return $this->_resource->value();
    }
    public function resource(): SecurityScopeResourceVO
    {
        return $this->_resource;
    }
    public function isResourceChanged(?self $original = null): bool
    {
        return $this->_resource->value() !== ($original ? $original->_resource->value() : null);
    }
    public function withResource(SecurityScopeResourceVO|string $resource): self
    {
        $copy = clone $this;
        $copy->_resource = SecurityScopeResourceVO::from($resource);
        return $copy;
    }
}
